<?phpclass Controller_Comment extends Core_Controller{	public function actionCreate ()	{		$comment = new Core_Comment();		$this->view = new Core_View();		$this->model = new Model_Comment();				if ($_SERVER['REQUEST_METHOD'] == 'POST') {						if (!empty($_POST['content'])){				$comment->content = trim($_POST['content']);			}			$comment->createTime=time();			$comment->postId = $_POST['postId'];			$this->model->createComment($comment);			header('Location: ../');		}		$this->view->render('View_Main.php', 'View_CommentCreate.php');	}	}