<?phpclass Controller_Comment extends Core_Controller{	private $error;		public function actionCreate ()	{		$comment = new Core_Comment();		$this->view = new Core_View();		$this->model = new Model_Comment();				if ($_SERVER['REQUEST_METHOD'] == 'POST') {						$comment->content = trim($_POST['content']);			$comment->createTime=time();			$comment->postId = $_POST['postId'];						$this->validate($comment);			if (empty($this->error)){				$this->model->createComment($comment);				header('Location: ../');			} else {				$this->view->render('View_CommentCreate.php','View_Template.php', array('error'=>$this->error));			}					}		$this->view->render('View_CommentCreate.php','View_Template.php');	}		private function validate (Core_Comment $comment)	{			if (empty($comment->content)){			$this->error[] = "Комментарий не должен быть пустым";		}	}	}