<?phpclass Core_FrontController{	protected $controller = "Controller_Main";	protected $action = "actionIndex";		public function __construct()	{		 $this->parseUri();	}		public function parseUri()	{		$path = parse_url($_SERVER["REQUEST_URI"], PHP_URL_PATH);		$path = explode("/", $path); 						if (!empty($path[2])) {            $this->setController($path[2]);        }        if (!empty($path[3])) {            $this->setAction($path[3]);        }	}		public function setController($controller)	{		$controller = "Controller_".ucfirst(strtolower($controller));		//if (class_exists($controller)) {			$this->controller = $controller;		//} 	}		public function setAction ($action) 	{		$action = "action".ucfirst(strtolower($action));		$this->action = $action;	}		public function run()	{		if(method_exists($this->controller, $this->action)) {			//call_user_func(array($this->controller,$this->action));			$contr = new $this->controller;			$act = $this->action;			$contr->$act();					} /*else {			throw new Exception('404 - method does not exist;');			/*echo $this->controller;			echo $this->action;		}*/			}} 