<?phpclass Core_PDOStatement extends PDOStatement{	public $profiler;		private function __construct($profiler)	{		$this->profiler = Core_DbConnection::getInstance()->profiler;	}		public function execute ($bound_input_params = NULL)	{		$start = microtime(true);//время начала запроса к бд		$result  = parent::execute($bound_input_params);		$end = microtime(true);//время конца запроса		$query = $this->queryString;//строка запроса		$rows = $this->rowCount();//количество строк, затронутых запросом		$this->profiler->addQuery($start,$end,$query,$rows);		return $result;	}}