<div class="b-pager"> 	<?php if ($firstVisiblePage>0):?>	<a href="?page=0">first</a>	<?php endif;?>	<?php	for ($i=$firstVisiblePage;$i<=$lastVisiblePage && $i<$pagesCount;$i++) {		$curr = $i;		if ($i == $currentPage){			echo "<span class='current'>{$curr}</span>";		} else {			echo "<a href='?page={$this->h($i)}'>{$curr}</a>";		}	}	?>		<?php if($lastVisiblePage<$pagesCount-1):?>	<a href="?page=<?=$pagesCount-1;?>">last</a>	<?php endif; ?></div>